package com.kwq.algorithm.algorithmics.string;

import java.util.HashMap;

/**
 * @auther : kongweiqiang
 * @DATE : 2020/12/23
 * @DESC : NO 387. 给定一个字符串，找到它的第一个不重复的字符，并返回它的索引。如果不存在，则返回 -1
 */
public class String_387 {

    public int firstUniqChar(String s) {
        int count = -1;
        if (s == null) {
            return count;
        }
        HashMap<Character,Integer> bitMap = new HashMap<>();
        Character c = null;
        for(int i = 0;i < s.length();i++){
            c = s.charAt(i);
            if(bitMap.containsKey(c)) {
                bitMap.put(c, bitMap.get(c) + 1);
            }else{
                bitMap.put(c, 1);
            }
        }
        for(int i = 0;i < s.length() ;i++){
            c = s.charAt(i);
            if(bitMap.get(c) == 1){
                count = i;
                break;
            }
        }
        return count;
    }

    public static void main(String[] args) {
        String s = "";
        String_387 main = new String_387();
        System.out.println(main.firstUniqChar(s));
        s = "loveleetcode";
        System.out.println(main.firstUniqChar(s));
    }
}
